function centerBgVideo(){$(".video-parallax-container").each(function(){var i=$(this).height(),e=($(this).children("video").height()-i)/2,n=$(this).height(),t=($(this).children("video").height()-n)/2;e>0&&$(this).children("video").css("margin-bottom",e+"px"),t>0&&$(this).children("video").css("margin-right",t+"px")})}$(document).ready(function(){centerBgVideo()}),$(window).on("resize",function(){centerBgVideo()});